import React from 'react';

const Message =(props)=>{
      const currentUser = this.props.currentUser;
         return (
            <div className="message" style={currentUser !== this.props.username ? {paddingLeft: 20}: {}}>
              <div className="message-username">
                 {props.username}
              </div>
              <div className="message-text">
                 {props.text}
              </div>
             </div>
           )

}

export {Message};
