import openSocket from 'socket.io-client';

const io =()=>{
    var socket = openSocket.connect("localhost:3030")
    socket.on("connect",()=>console.log("connected"))

    function sendMessage(msg){
        socket.emit("newMessage",{message:msg})
    }
}

export default io;
