import React from 'react';

const Message =(props)=>{
      const currentUser = this.props.currentUser;
      if(currentUser !== this.props.username){
         return (
            <div className="message">
              <div className="message-username">
                 {props.username}
              </div>
              <div className="message-text">
                 {props.text}
              </div>
             </div>
           );
      }

}

export {Message};
